INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('b3179f2f-5e8b-4279-afd8-31a99ff42f5f', 'flowering-maple', '["Abutilon hybridum"]', 'The Flowering Maple (Abutilon hybridum) is an amazing plant species for its hardiness, its unique flowers, and its ability to brighten up any landscape. It''s a very low maintenance plant that flourishes in full sun and well drained soils. Its flowers are very striking and come in many different colors - the most common being white and yellow, with a contrasting dark center. This plant is ideal for those looking to add some vibrant color to their gardens without a lot of work. It can easily grow up to six feet and can even tolerate partial shade. The Flowering Maple is definitely an attention-grabber and will be sure to make any garden stand out.', 'https://perenual.com/storage/species_image/425_abutilon_hybridum/og/24527654869_ac712f58c7_b.jpg', 'Frequent', '["full sun", "part shade"]', 'Low', 0, '["Stem Propagation", "Root Cutting", "Air Layering Propagation", "Grafting Propagation", "Seed Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('de2c23d2-8473-459c-8b7f-90f9c6da7e43', 'flowering-maple', '["Abutilon hybridum ''Bella Red''"]', 'Flowering-maple (Abutilon hybridum ''Bella Red'') is a magnificent plant species with its beautiful red bell-shaped flowers and bright green, maple-like foliage. Growing to a manageable height of around 1-3 feet (30-90 cm), this drought-tolerant, self-sowing annual is perfect for adding color to any garden. Its blooms appear in an array of vibrant colors, from magenta to yellow, that light up any outdoor setting. Its gorgeous flowers also attract butterflies and hummingbirds, making it a great choice for a wildlife garden. Combined with its hardiness, this species certainly is a magnificent addition to any garden.', 'https://perenual.com/storage/species_image/426_abutilon_hybridum_bella_red/og/abutilon-mallow-flower-red.jpg', 'Average', '["full sun", "part shade"]', 'Low', 0, '["Stem Propagation", "Root Cutting", "Air Layering Propagation", "Grafting Propagation", "Division"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('de272793-dcd7-4624-ae87-fd6f0e8a7918', 'flowering-maple', '["Abutilon ''Moonchimes''"]', 'The flowering-maple (Abutilon ''Moonchimes'') is an amazing plant species with unique flowers for any garden. It produces purple, bell-shaped flowers with white and yellow highlights that simply mesmerize in the summer sun. The leaves are large and maple-shaped, which adds a nice contrast with the bell-shaped flowers. It''s a great addition to any garden due to its beauty, low-maintenance needs, andzone 6 hardiness. Furthermore, it grows a well in both full sun or partial shade, making it an ideal choice for a variety of garden spaces. Get your own flowering-maple today and enjoy its special beauty in your own backyard.', 'https://perenual.com/storage/species_image/427_abutilon_moonchimes/og/Abutilon_x_hybridum_27Moonchimes27_Flower_2800px.jpg', 'Average', '["full sun", "part shade"]', 'Low', 0, '["Stem Propagation", "Root Cutting", "Air Layering Propagation", "Division", "Seed Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('4cd6068c-0b68-4fd0-aa36-981330f7c131', 'flowering-maple', '["Abutilon pictum ''Gold Dust''"]', 'The ''Gold Dust'' variety of flowering-maple is an amazing example of a unique, striking ornamental. The rounded green leaves are generously dusted with gold highlights, making the foliage a source of interest in its own right. In summer, flowers resembling small orange bells appear in abundance, adding to the landscape. Unlike many flowering plants, this variety of flowering-maple is forgiving of colder climes and can be grown in zone 7, making it ideal for a variety of landscaping settings. This hardy species is easily maintained with a simple pruning and can usually be seen showing off its stunning foliage and flowers for several years.', 'https://perenual.com/storage/species_image/428_abutilon_pictum_gold_dust/og/Redvein_Abutilon.jpg', 'Average', '["full sun"]', 'Low', 0, '["Stem Propagation", "Root Cutting", "Air Layering Propagation", "Division", "Grafting Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('32eb961c-a0bc-4717-bc7a-49718e19dc14', 'Jacob''s coat', '["Acalypha wilkesiana"]', 'Jacob''s Coat (Acalypha wilkesiana) is an amazing foliage plant with colorful leaves that will make a bold statement in your garden. Its leaves emerge in the spring with attractive shades of green and red, and turn pink, purple, yellow, and even silver as the season progresses. The plant itself is easy to care for, requiring minimal watering and very little pruning. In addition, it is a hardy plant and is nearly immune to pests and disease problems. If you want to make your garden stand out, consider planting a Jacob''s Coat! Its multi-colored leaves will be sure to garner attention and admiration.', 'https://perenual.com/storage/species_image/434_acalypha_wilkesiana/og/24945289366_5035e5d0a9_b.jpg', 'Frequent', '["full sun", "part shade"]', 'Low', 0, '["Stem Propagation", "Air Layering Propagation", "Division"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('786ca7b0-979a-433b-9622-afeb209337c1', 'desert rose', '["Adenium obesum"]', 'The desert rose is an amazing plant species that adds vibrant, flowering color to any space. This drought-tolerant succulent requires minimal water and a sunny, dry environment, making it ideal for most desert and dry climate gardens. Its showy blooms range in color from hot pink and vibrant red to stunning white and yellow shades. The striking ombre of the adenium obesum''s swollen base and graceful, slender stems make it a true stunner. It''s also highly toxin resistant and can handle a wide range of temperatures and environmental conditions. No wonder it''s quickly becoming one of the most popular flowers around!', 'https://perenual.com/storage/species_image/540_adenium_obesum/og/9244335137_6d662ed77c_b.jpg', 'Minimum', '["full sun"]', 'Low', 0, '["Grafting Propagation", "Cutting", "Seed Propagation", "Air Layering Propagation", "Division"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('d51523e6-cd9f-49ac-804a-c660313f75fd', 'maidenhair fern', '["Adiantum capillus-veneris"]', 'The maidenhair fern (Adiantum capillus-veneris) is a beautiful and unique species of fern with feathery, wispy fronds that give an air of delicacy and grace to any space. Its elegant fronds are complemented by lovely fan-like foliage made up of numerous small leaflets, giving it an exquisite lacy appearance that adds beauty to any garden. Its hardy nature also makes it a great choice for those looking for something that can tolerate a variety of temperatures, thriving in colder climates despite its tropical look. It''s an amazing plant species that''s sure to be a stunning addition to any outdoor area.', 'https://perenual.com/storage/species_image/543_adiantum_capillus-veneris/og/49636882698_cb4fa4c685_b.jpg', 'Average', '["part shade", "part sun/part shade"]', null, 0, '["Division", "Cutting", "Spore Propagation", "Layering Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('b59d2942-922a-42a2-a6bf-ad1181ea12cf', 'delta maidenhair fern', '["Adiantum raddianum"]', 'The Delta Maidenhair Fern is an amazing species of fern with a unique look. Its fronds create a delicate-looking cloud of bright-green foliage, adding a tropical feel to any environment. Its small, triangular leaves grow in whorls of three on the ends of thin black stems, forming an intricate pattern. Its hardiness makes it one of the most versatile ferns for both indoor and outdoor growth. Its light texture and lush colours make it an ideal houseplant for a highlight corner of a home or office. With proper care and attention, it will bring an elegant note of greenery and serenity to any space.', 'https://perenual.com/storage/species_image/546_adiantum_raddianum/og/48134564587_c4562175cb_b.jpg', 'Average', '["part shade"]', 'Low', 0, '["Division", "Cutting", "Spore Propagation", "Layering Propagation"]', 'High');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('200eeeb6-1783-4134-aaa8-0017a0357642', 'urn plant', '["Aechmea fasciata"]', 'The urn plant (Aechmea fasciata) is an amazing species because of its striking foliage and interesting growth habit. It is one of the few bromeliads that produces structural leaves in a rosette pattern and colorful flower spikes. Its evergreen leaves can range from deep green to silver and can feature stripes, spots, or variegation. Additionally, this species is known to produce offsets, which makes it easier to propagate. And, finally, its vivid red, pink, or yellow flowers, depending on the variety, are stunning and last for months.', 'https://perenual.com/storage/species_image/549_aechmea_fasciata/og/51446529161_03b99a881f_b.jpg', 'Average', '["part sun/part shade"]', 'Low', 0, '["Seed Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('bcd7e1d7-97cb-4573-9bee-122b1591d0d8', 'stalked aeonium', '["Aeonium undulatum"]', 'Stalked Aeonium, or Aeonium undulatum, is an amazing succulent species with its showy yellow blooms and striking foliage. Its bright purple-green foliage has stout stems that produce a neat rosette pattern, while the yellow flowers appear in winter and early spring. The plant does best in well-drained soil and medium light, making it suitable for all types of gardens. Stalked Aeonium is drought-tolerant and requires minimal care. Its beauty and hardiness make it a great addition to any garden, bringing color and style to any landscape. With its low-maintenance requirements, Stalked Aeonium is an amazing succulent species worth adding to any garden.', 'https://perenual.com/storage/species_image/551_aeonium_undulatum/og/023e2628848775ea95af34a39eaee4d60fab11ed.jpg', 'Average', '["full sun", "part shade"]', 'Low', 0, '["Cutting", "Division", "Seed Propagation", "Layering Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('8482cae4-6a88-4c97-a076-fc00a12b07a9', 'lipstick plant', '["Aeschynanthus radicans"]', 'The lipstick plant (Aeschynanthus radicans) is an amazing species that stands out with its unique color, texture and shape. Its waxy leaves have attractive colors, making it pleasing to look at, while its clusters of bright red and orange flowers look like tubes of lipstick! This plant is very easy to care for, being a low-maintenance, tropical evergreen climber. It does best in bright, indirect sunlight and in moist, well-drained soil. Additionally, it is known for its long flowering period, adding a splash of color to your indoor or outdoor spaces. With its beautiful colors, distinctive leaves, and ease of care, the lipstick plant is a fantastic addition to any garden.', 'https://perenual.com/storage/species_image/552_aeschynanthus_radicans/og/29949422675_2671589cdf_b.jpg', 'Average', '["part shade", "full sun"]', 'Low', 0, '["Stem Propagation", "Air Layering Propagation", "Division", "Leaf Cutting"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('a42941e3-3bc4-4b2f-adec-7e6c7125229a', 'Philippine evergreen', '["Aglaonema commutatum"]', 'Philippine evergreen (Aglaonema commutatum) is truly an amazing plant for many reasons. It has a beautiful, glossy foliage that is decorated with silver brush strokes, giving it an eye-catching appearance. It is also considered one of the most versatile and easy-to-care-for houseplants. It grows quickly, is tolerant of low light, and surviving drought periods. Additionally, the Philippine evergreen is known to be an air-purifying plant, ridding the air of many harmful toxins. It provides a lush, jungle-like atmosphere to your home or office with minimal effort.', 'https://perenual.com/storage/species_image/625_aglaonema_commutatum/og/24798632751_3a039ecbc6_b.jpg', 'Average', '["part shade", "full shade"]', 'Low', 0, '["Stem Propagation", "Division", "Air Layering Propagation", "Leaf Cutting"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('ac9d7c85-31bc-4d51-b4a4-f9133653afcc', 'Chinese evegreen', '["Aglaonema ''Cutlass''"]', 'Chinese evergreen (Aglaonema ''Cutlass'') is an amazing plant that is sure to bring joy and beauty to any home. It’s low-maintenance and requires minimal care, making it perfect for novice gardeners. It’s easy to propagate and comes in vibrant colors featuring streaks of white, cream, and pink. This stunning foliage reduces stress and can help improve air quality by naturally filtering toxins from the air. Chinese evergreen is a great way to add a touch of luxury to any home décor.', 'https://perenual.com/storage/species_image/626_aglaonema_cutlass/og/2560px-Chinese_Evergreen_28Aglaonema_nitidum_27Silver_Queen2729_1.jpg', 'Average', '["part shade", "full shade"]', 'Low', 0, '["Division", "Air Layering Propagation", "Stem Propagation", "Leaf Cutting"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('35779d53-4fb8-4c05-8284-71567785ff3d', 'Chinese evergreen', '["Aglaonema ''Silver Queen''"]', 'The Chinese evergreen (Aglaonema ''Silver Queen'') is one of the most popular houseplants because of its ease of care and gorgeous foliage. Its lush, dark green leaves can be splashed with colors of silver, gray, and white, making it a stunning addition to any home. Not only is it beautiful, but it is also very low maintenance, requiring little light and water to thrive. Additionally, it is known to purify the air and help reduce airborne toxins. These long-living plants can bring a bit of nature indoors and put a pleasant pop of color in any home.', 'https://perenual.com/storage/species_image/628_aglaonema_silver_queen/og/24798632751_3a039ecbc6_b.jpg', 'Average', '["part shade", "full shade"]', 'Low', 0, '["Division", "Air Layering Propagation", "Stem Propagation", "Leaf Cutting"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('137b466f-3ccf-4979-a2ac-334eb85a5cb2', 'onion', '["Allium cepa"]', 'Onion (Allium cepa) is an amazing and versatile vegetable that has been used in cooking for centuries. Its taste is powerful yet complex, and it adds flavor and texture to many dishes. Onions are incredibly nutritious and are packed with essential vitamins and minerals including vitamin C, folate, iron, and potassium. Not only that, but onions may also have antiseptic, antibiotic, and antioxidant properties. They are an excellent source of dietary fiber and possess anti-inflammatory compounds. Onions are easy to grow in any garden, and they come in a variety of sizes, shapes and colors, making them a unique and beautiful ornamental plant. Its versatility in cooking, nutritional content, and medicinal properties combined with its decorative appearance make the onion a truly remarkable species.', 'https://perenual.com/storage/species_image/667_allium_cepa/og/24466615444_a161c175d3_b.jpg', 'Average', '["full sun"]', 'High', 0, '["Seed Propagation", "Tissue Culture"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('95cc0bc3-59ef-49d2-95e4-7fe2a4b5b676', 'elephant''s ear', '["Alocasia amazonica"]', 'The elephant''s ear (Alocasia amazonica) is an amazing plant species. Its striking foliage features parabolic shaped leaves with bright green and silver/blue-green veins on the surface. Its white veins make the plant appear more vibrant, especially when provided with enough sunlight. Its size can range from one to two feet, making it an immediate attention-grabbing and decorative addition to any garden. It can also be grown indoors and is known for its air purifying capabilities. With its stunning, intricate foliage and its ability to add a splash of life and vivid color wherever you choose, the elephant''s ear is an amazing species to add to your garden.', 'https://perenual.com/storage/species_image/710_alocasia_amazonica/og/24444063964_a3054131aa_b.jpg', 'Average', '["part shade", "part sun/part shade"]', 'High', 1, '["Division", "Air Layering Propagation", "Stem Propagation", "Offsets", "Seed Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('e2364563-fc9e-4cf9-9615-0e8773d00e25', 'Amazonian elephant''s ear', '["Alocasia amazonica ''Polly''"]', 'The Amazonian elephant’s ear (Alocasia amazonica ''Polly'') is a unique and stunning plant that demands attention! Its lush, velvety leaves are an eye-catching bright green, with prominent ivory undersides. The deep purple veins running through the foliage lend a striking contrast. With large and distinctive heart-shaped leaves resembling an elephant’s ear, it establishes a striking presence in any room or garden. Its tall, glossy stems create a strikingly elegant look. No wonder it’s the preferred choice of gardeners! Its easy to care for and its exotic beauty will never go out of style.', 'https://perenual.com/storage/species_image/711_alocasia_amazonica_polly/og/pexels-photo-12419491.jpg', 'Frequent', '["part shade"]', 'High', 1, '["Division", "Air Layering Propagation", "Stem Propagation", "Offsets", "Seed Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('038a87ad-c282-4398-bf02-5294bc7435c7', 'elephant''s ear', '["Alocasia ''Frydek''"]', 'The elephant''s ear (Alocasia ''Frydek'') is a truly amazing addition to any garden or outdoor area. Its large, eye-catching, veined foliage is a unique deep green and its easy-to-manage size means it can fit almost anywhere. Furthermore, its glossy leaves are sure to be a pleasure to look at under the sun. On top of its exotic beauty, this plant is known to attract pollinators such as butterflies and hummingbirds, making it a great choice for anyone looking for an addition that adds beauty and liven up the design. Adding to its appeal, this plant is easy to care for and requires minimal attention. With its showstopping beauty and minimal maintenance, the elephant''s ear is sure to be a beautiful addition to any outdoor space.', 'https://perenual.com/storage/species_image/712_alocasia_frydek/og/Alocasia_micholitziana.jpg', 'Average', '["part shade"]', 'High', 1, '["Division", "Air Layering Propagation", "Stem Propagation", "Offsets", "Seed Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('874064f2-d5ae-471c-a344-a414f0e03fa7', 'elephant''s ear', '["Alocasia (group)"]', 'The Elephant’s Ear is a truly impressive plant species, featuring wide, glossy leaves that look strikingly similar to an elephant''s ear. This majestic, low-maintenance plant will add a real touch of class to any home. The Elephant’s Ear is also known for its quick growth rate, with each leaf growing up to four feet wide and long, making it an eye-catching addition that can fill entire rooms. Colorful flowering varieties of the Elephant’s Ear create a beautiful display of vibrant deep green or purple leaves with stunning, contrasting white veins. So if you’re looking for a dramatic addition to your home, the Elephant’s Ear is definitely a great choice.', 'https://perenual.com/storage/species_image/713_alocasia_group/og/24444063964_a3054131aa_b.jpg', 'Average', '["full sun", "part shade"]', 'High', 1, '["Division", "Air Layering Propagation", "Stem Propagation", "Offsets", "Seed Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('7b2acbae-a004-4ba4-baea-91d005d339f0', 'elephant''s ear', '["Alocasia LOW RIDER"]', 'Elephant''s Ear (Alocasia Low Rider) is a stunning and unique plant species characterized by its lush, lush, dark-green foliage. Its signature "elephant ear" shaped leaves and thick stems reach huge proportions, making this plant a show-stopper in any garden or home setting. Also known as "Alocasia Low Rider", this particular species of elephant''s ear is particularly remarkable as its lower leaves tend to be shorter and wider than standard forms, making it an ideal "ground cover" choice. Unusual, attractive and low-maintenance, this eye-catching ornamental perennial is sure to serve as an impressive feature in any landscape.', 'https://perenual.com/storage/species_image/714_alocasia_low_rider/og/8415446715_799e70b503_b.jpg', 'Average', '["full sun", "part shade"]', 'Slow', 1, '["Division", "Air Layering Propagation", "Stem Propagation", "Offsets", "Seed Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('15caab2f-ebf6-40d0-bf59-bd610909d630', 'giant elephant''s ear', '["Alocasia macrorrhizos"]', 'The giant elephant''s ear (Alocasia macrorrhizos) is an amazing plant species because of its large and broad leaves. Its leaves can grow to be over 6 feet long and 3 feet wide. It is a tropical plant, preferring humid environments and is great for creating a tropical feel in your home. The foliage is also a striking addition to your garden, with its huge, dark green leaves veined with lighter green veins creating an interesting pattern. Its leaves also offer some shade and relief from the hot sun on hot summer days.', 'https://perenual.com/storage/species_image/715_alocasia_macrorrhizos/og/33234440338_8b31463973_b.jpg', 'Average', '["part shade"]', 'High', 1, '["Division", "Stem Propagation", "Air Layering Propagation", "Offsets", "Seed Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('fc11f773-b2c2-4b43-a07f-f18afdfb8496', 'elephant''s ear', '["Alocasia ''Portora''"]', 'The Elephant''s Ear (Alocasia ''Portora'') is an amazing ornamental plant for the garden. Its broad, deep-green, heart-shaped leaves have an iridescent quality that channels the beauty of an elephant''s ears. Strikingly contrasting veins visibly contrast the lushness of this species, and further compliment its breathtaking silhouette. With good watering and care, this foliage plant can reach a height of 8-10 feet, making it perfect for larger garden spaces. Its bright ambiance will provide the perfect accent to any landscape and its hearty leaves will retain its vibrancy throughout any season.', 'https://perenual.com/storage/species_image/717_alocasia_portora/og/50513640218_140cd83c95_b.jpg', 'Average', '["full sun", "part shade"]', 'Low', 1, '["Division", "Air Layering Propagation", "Stem Propagation", "Offsets", "Seed Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('a40c7603-3f97-49da-848e-0bdcf527ec2f', 'elephant''s ear', '["Alocasia ''Sarian''"]', 'Elephant''s ear (Alocasia ''Sarian'') is truly a sight to behold. Its large, heart-shaped leaves with deep green veins create a dramatic tropical focal point for any garden. Its magnificent size can reach up to five feet in height, with the leaves growing up to one foot wide. When touched, the leaves produce a unique texture that feels velvety and silky. This plant is incredibly easy to care for and can tolerate a range of light conditions. With its impressive size and vibrant zebra-like pattern, this stunning plant is sure to be the show-stopping centerpiece of any exotic garden.', 'https://perenual.com/storage/species_image/718_alocasia_sarian/og/466458488_b9358efed2_z.jpg', 'Average', '["part sun/part shade"]', 'High', 1, '["Division", "Air Layering Propagation", "Stem Propagation", "Offsets", "Seed Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('f5893bc9-b184-437c-9d36-bcec155eb4fb', 'aloe', '["Aloe harlana"]', 'Aloe harlana, also known as the yellow-flowering barbed aloe, is a plant species that is simply remarkable. It has a remarkable longevity and ability to thrive in arid regions, making it a great choice for those looking for a minimal maintenance houseplant. Its bright yellow flowers are composed of hundreds of wispy petals, giving an almost ethereal ambiance to any room it’s placed in. The leaves of an aloe harlana are thick yet supple and provide powerful medicinal properties, as well as being an effective deterrent against pests. All in all, the Aloe harlana is an easy-to-care for plant with a multitude of benefits that make it a must-have in any home.', 'https://perenual.com/storage/species_image/721_aloe_harlana/og/5582077315_8d613454aa_b.jpg', 'Minimum', '["part shade", "part sun/part shade"]', 'Low', 0, '["Offsets", "Leaf Cutting", "Division", "Seed Propagation", "Grafting Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('8b4eea12-260e-4781-bba6-7357e5f9de62', 'aloe', '["Aloe suzannae"]', 'Aloe suzannae, otherwise known as ''pocket aloe'', is an amazing plant species. It is native to Namibia and South Africa, with its unique spikey leaves forming a rosette shape. It is well known for its health benefits and its resilience when it comes to droughts. It loves the sun and can survive in a variety of conditions, requiring minimal care. Its beautiful orange-yellow flowers appear in summer, adding an extra special touch. Aloe susannae is an amazing addition to any garden, adding a vibrant and colourful touch.', 'https://perenual.com/storage/species_image/727_aloe_suzannae/og/23704519300_5ec1807143_b.jpg', 'Minimum', '["full sun", "part sun/part shade"]', 'Low', 0, '["Offsets", "Leaf Cutting", "Seed Propagation", "Division"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('9a4ca6f5-0cc7-4b22-9163-202b1715bf4e', 'aloe', '["Aloe vera"]', 'Aloe vera is a fantastic plant species known for its many amazing health benefits. Consisting of thick leaves with a gel-like interior, aloe vera has long been used as a traditional remedy for treating skin conditions. It is a great source of vitamins, minerals, and amino acids, making it a great natural remedy for common ailments. Aloe vera is also incredibly versatile, with the gel and sap being used for everything from digestive disorders to eye health. It is even believed to reduce inflammation and improve circulation. With its myriad of benefits, aloe vera is definitely an amazing plant species.', 'https://perenual.com/storage/species_image/728_aloe_vera/og/52619084582_6ebcfe6a74_b.jpg', 'Minimum', '["full sun"]', 'High', 0, '["Division"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('91967fcc-1de3-44aa-95f2-a78320ba9aae', 'Joseph''s coat', '["Alternanthera ficoidea"]', 'Joseph''s coat (Alternanthera ficoidea) is an amazing plant that stands out from other species because of its vibrant, multi-colored foliage. Its leaves display shades of red, yellow, white, and purple, making it a dynamic addition to any landscape or container garden. It is extremely simple to maintain and can thrive in a variety of soil conditions. Joseph''s coat can withstand extremes in temperature, drought and semi-shade without compromising its color. It also blooms small flowers that last until late fall adding to the vibrant display. It is a great choice for any gardener looking to add stunning color and texture to any space.', 'https://perenual.com/storage/species_image/747_alternanthera_ficoidea/og/48756446498_fc5901c40d_b.jpg', 'Frequent', '["full sun", "part shade"]', 'High', 0, '["Stem Propagation", "Division", "Layering Propagation", "Seed Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('ce52ce9f-5c49-4591-b4ca-0c510d91685c', 'anthurium', '["Anthurium andraeanum"]', 'Anthurium andraeanum is an amazing plant species for many reasons. It has a brightly coloured spathe, which can be orange, white and yellow, as well as long-lasting flowering. It is a long-lived species, and its blooms can remain intact for several months. This attractive plant is also easy to care for and can thrive in indirect light and humid environments. Its flowers bloom regularly and even if the plant is not fertilised, it can still keep thriving. Furthermore, the Anthurium andraeanum is perfect for those looking for a unique, eye-catching houseplant to add to their home. In conclusion, Anthurium andraeanum is a stunning and resilient species with distinctively vibrant flowers.', 'https://perenual.com/storage/species_image/855_anthurium_andraeanum/og/49388458462_0ef650db39_b.jpg', 'Average', '["part sun/part shade"]', 'High', 0, '["Division", "Air Layering Propagation", "Stem Propagation", "Leaf Cutting", "Seed Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('3528ab2c-6740-47a3-a607-2ed9dd980bfe', 'anthurium', '["Anthurium crassinervium"]', 'Anthurium crassinervium is an amazing species of plant. It is an evergreen flowering plant with stunning heart-shaped red, blanket shaped bracts. Its glossy green leaves grow to a length of up to 8 inches, making it a great choice for any indoor or outdoor setting. Furthermore, the Anthurium has air-purifying capabilities and can help to eliminate toxins from the air. This species does not require a great deal of light or water making it easy to maintain. Plus, with its long life-span and minimal maintenance requirements, this plant is sure to bring beauty to your home for years to come.', 'https://perenual.com/storage/species_image/856_anthurium_crassinervium/og/2560px-Anthurium_crassinervium.jpg', 'Frequent', '["full sun", "part shade"]', 'Low', 0, '["Air Layering Propagation", "Stem Propagation", "Division", "Offsets", "Seed Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('74ab7c1e-486e-446f-9038-46617a8410b8', 'zebra plant', '["Aphelandra squarrosa"]', 'The zebra plant (Aphelandra squarrosa) is an amazing species for many reasons. It is an evergreen perennial shrub native to tropical regions of America, with a unique and beautiful foliage that is composed of mid-green leaves with bold, yellow-centered darker green veins. Its foliage is incredibly eye-catching and provides an exotic yet stylish look to any garden. Although it does not bloom often, when it does, it puts forth amazing blooms with creamy white petals and yellow centers, looking almost like a daisy. While not overly fuss, it does need a good amount of bright light and regular watering. With the right upkeep, this species will thrive in any environment and provide a unique and attractive look.', 'https://perenual.com/storage/species_image/861_aphelandra_squarrosa/og/9216112108_2bb5c403b3_b.jpg', 'Average', '["part shade", "full shade"]', 'Low', 0, '["Greenwood Cuttings"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('379a01f8-456f-4fcf-976d-bdbd220be69e', 'trailing snapdragon', '["Asarina procumbens"]', 'Trailing snapdragon is a truly amazing species! With gorgeous blooms of lavender or white, it will add beauty to any garden. It is also resilient and easy to care for as it doesn''t need a lot of water, but can tolerate drought conditions. The branches are flexible enough to allow you to use it for ground covers, planters, window boxes, and more. It stands out from other plants due to its unique habit of trailing over ledges, containers and retaining walls resulting in a stunning effect. Asarina Procumbens can add color to your landscape throughout the summer and provides you with eye-catching textures.', 'https://perenual.com/storage/species_image/1001_asarina_procumbens/og/9240253872_46be573382_b.jpg', 'Average', '["full sun", "part shade"]', 'Low', 0, '["Stem Propagation", "Layering Propagation", "Division", "Seed Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('6a732a45-c080-43f6-a75e-f9dd907d4521', 'Sprenger''s asparagus fern', '["Asparagus densiflorus"]', 'Sprenger''s asparagus fern (Asparagus densiflorus) is an amazing evergreen perennial that is perfect for sunny areas as it often blooms in late spring to summer. It is easy to care for, with arching stems of small, airy green foliage, and tiny white flowers. This makes a lovely ground cover and can also be potted for tabletop displays or hanging baskets. Hardy and drought tolerant, it requires minimal care and will thrive with light fertilizing during the growing season. A true delight to behold, Sprenger''s asparagus fern is the perfect addition to any garden.', 'https://perenual.com/storage/species_image/1023_asparagus_densiflorus/og/49289066276_4bbe4e5b94_b.jpg', 'Average', '["part shade"]', 'Low', 0, '["Division", "Cutting", "Seed Propagation", "Layering Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('ea54921b-95c6-4a27-a031-677d866f55f3', 'asparagus fern', '["Asparagus densiflorus ''Myersii''"]', 'The asparagus fern, formally known as Asparagus densiflorus ''Myersii'', is an amazing plant species. It grows quickly and produces delicate, feathery foliage that resembles ferns, making it a great choice for creating a lush, tropical look in the home or garden. Its bright green leaves also contain unique properties that help to purify the air, making it an ideal plant to keep indoors. The asparagus fern is easy to maintain, requiring minimal care and little watering, and it also thrives in direct sunlight and low temperatures. Its resilience and air-purifying qualities make it a spectacular addition to any home.', 'https://perenual.com/storage/species_image/1024_asparagus_densiflorus_myersii/og/24611964210_ce3afbb0e8_b.jpg', 'Average', '["part shade"]', 'High', 0, '["Division", "Cutting", "Seed Propagation", "Layering Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('9f716c0c-6573-4786-b3c8-41d22b86f19c', 'asparagus fern', '["Asparagus densiflorus ''Sprengeri''"]', 'Asparagus fern is an amazing species of plant that easily stands out among the others. These incredibly unique feathery ferns can bring a truly exotic and wild look to any garden. Their vibrant branch-like foliage and delicate white flowers that bloom in the summer can instantly add a stunning focal point to any space. Asparagus fern is also incredibly easy to care for, requiring little maintenance and water, making it great for novice gardeners. What’s more, these plants are also drought-tolerant and can survive in full or partial shade. Therefore, it’s a great choice for those looking to brighten up a corner in their garden. Asparagus fern is truly a gorgeous and low-maintenance addition to any garden.', 'https://perenual.com/storage/species_image/1025_asparagus_densiflorus_sprengeri/og/24818361201_11881d4f76_b.jpg', 'Minimum', '["part shade"]', 'High', 0, '["Seed Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('29d01793-511f-4e3f-bf7b-875df285f4f0', 'asparagus fern', '["Asparagus setaceus"]', 'The asparagus fern (Asparagus setaceus) is an amazing plant for any garden. Boasting attractive, wispy foliage, this delicate fern looks beautiful in the garden or in containers. Not only does it look great, it’s also easy to care for. It prefers bright, indirect light and can even survive low light conditions. Asparagus ferns are also easy to propagate, making them a great addition to any flower bed. With its hardy nature and attractive foliage, this is an ideal choice for both experienced and beginner gardeners.', 'https://perenual.com/storage/species_image/1031_asparagus_setaceus/og/24274026853_98d1a2733a_b.jpg', 'Frequent', '["part shade"]', 'Low', 0, '["Seed Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('c471f0e5-6faa-4d08-b6e1-072c9ebc8437', 'cast-iron plant', '["Aspidistra elatior"]', 'The cast-iron plant (Aspidistra elatior) is an amazing plant species due to its resilience and endurance. This plant is almost impossible to kill, since it can withstand extreme temperatures, low light levels, drought, and poor soil. It also requires very little attention to thrive, making it an ideal choice for busy or novice gardeners. Additionally, it has beautiful broad, dark green leaves which create an elegant, yet durable presence in any room.Featuring extremely low maintenance, this hardy and attractive plant is the perfect addition to any environment.', 'https://perenual.com/storage/species_image/1036_aspidistra_elatior/og/e5c6a91ae63e816bdb0d287f549bd74829e09923.jpg', 'Average', '["part shade", "full shade"]', 'Low', 0, '["Division", "Cutting", "Air Layering Propagation", "Seed Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('bfd2dc88-97d0-4272-ac8e-13c93cc1c79e', 'bird''s nest fern', '["Asplenium nidus ''Antiquum''"]', 'The Bird''s Nest Fern (Asplenium nidus ''Antiquum'') is a rare and unique species of fern that is a must-have addition to anytropical garden. Its stunning, wide-spreading fronds make it an attractive focal point, while its resistance to pests and diseases make it perfect for busy gardeners. Its foliage is a delight - deep green and glossy, with leathery leaves that are ruffled along the edges. Its yellow patches on the fronds add further to its aesthetic appeal and help it blend into other foliage. Its ability to adapt to different environments makes it a great choice for both indoor and outdoor spaces, making it a great choice for any tropical themed garden.', 'https://perenual.com/storage/species_image/1038_asplenium_nidus_antiquum/og/29457334967_82a2d8789c_b.jpg', 'Average', '["full sun", "part shade", "full shade"]', 'Low', 0, '["Division", "Cutting", "Spore Propagation", "Tissue Culture"]', null);
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('e526b25e-ba9d-49fe-8390-286fdc349004', 'Japanese painted fern', '["Athyrium niponicum var. pictum"]', 'The Japanese Painted Fern is an amazing species of fern that stands out with its yellow fronds that are beautifully accented with green and silver markings. This plant can bring life to any garden with its elegant and delicate look and its ability to thrive in both well-drained soils and shady spots. It is an excellent choice of fern to help create interesting texture in garden beds and it''s quite disease and pest resistant. The Japanese Painted Fern is a stunning addition to any landscape, with its delicate fronds swaying in the breeze and its eye-catching yellow and silver coloring.', 'https://perenual.com/storage/species_image/1130_athyrium_niponicum_var_pictum/og/27024762402_1df320b955_b.jpg', 'Average', '["deep shade", "filtered shade", "part sun/part shade"]', 'High', 0, '["Division"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('bd573216-6226-4f47-9cbb-04eaf3e07a8d', 'Japanese painted fern', '["Athyrium niponicum var. pictum ''Metallicum''"]', 'The Japanese painted fern (Athyrium niponicum var. pictum ''Metallicum'') is an amazing plant owing to its distinct metallic silver stripes that adorn its feathery, triangular fronds. The contrast of striking silver with deep green leaves make it a captivating accent to any garden or home. It is an easy-care fern that prefers moist, well-drained soil and partial to full shade. The fronds grow to be 12 inches wide and 24inches in length and are ideal for under-planting larger flowering shrubs. It''s an extremely versatile plant that adds an exotic touch to any home or garden.', 'https://perenual.com/storage/species_image/1133_athyrium_niponicum_var_pictum_metallicum/og/Athyrium_niponicum_pictum_1.jpg', 'Average', '["deep shade", "filtered shade", "part sun/part shade"]', 'High', 0, '["Division"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('8acd6d00-c93d-4f7c-a007-d2708dbe6ec6', 'spotted laurel', '["Aucuba japonica ''Mr. Goldstrike''"]', 'Spotted laurel (Aucuba japonica ''Mr. Goldstrike'') is an amazing plant species because of its striking appearance. Its glossy, evergreen leaves consist of yellow spots on a dark green background, making for a beautiful landscape feature all year round. It is also extremely hardy and can thrive in shady spots that other plants struggle to survive in. As if that wasn’t enough, this species is able to tolerate a wide range of soil types and is generally low-maintenance once established. All these qualities combined make spotted laurel an ideal choice for the garden.', 'https://perenual.com/storage/species_image/1147_aucuba_japonica_mr_goldstrike/og/50589856477_0b4ceffe2a_b.jpg', 'Frequent', '["full shade", "part shade"]', 'Low', 0, '["Cutting", "Layering Propagation", "Division", "Grafting Propagation", "Seed Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('b40b8eea-2888-402a-814e-7da7f8917760', 'spotted laurel', '["Aucuba japonica ''Suruga Benten''"]', 'The spotted laurel (Aucuba japonica ''Suruga Benten'') is an amazing plant due to its striking foliage and hardiness. This evergreen shrub has gorgeous glossy leaves that are mottled in dark green, yellow, and sometimes white. It produces a deep pink flower in the shape of a star during the spring season. With its tolerance to both humid and dry climates, the plant can grow in various outdoor and indoor settings. Plus, it’s also drought-tolerant and pest-resistant. With its eye-catching foliage and easy maintenance, the spotted laurel is an ideal pick for brightening up any garden space.', 'https://perenual.com/storage/species_image/1149_aucuba_japonica_suruga_benten/og/50589856477_0b4ceffe2a_b.jpg', 'Average', '["part sun/part shade", "full sun"]', 'Low', 0, '["Cutting", "Layering Propagation", "Division", "Grafting Propagation", "Seed Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('dfe8150e-878f-451b-aee1-bae9e2ee3113', 'spotted laurel', '["Aucuba japonica ''Variegata''"]', 'Spotted Laurel (Aucuba japonica ''Variegata'') is an evergreen shrub, known for its signature leaves of green and yellow-gold with blotched variegation. Growing to a potential height of 3-4 feet and width of 2-4 feet, it is an easy-to-care shrub, perfect for a low-maintenance garden. This is a ‘shade-thriving’ plant and is suitable for a wide range of climates, making it a great choice for any garden. Blooms of small, green-yellow flowers appear in the late winter and are followed by bright red berries. Spotted Laurel is a great way to bring beautiful, lively colors to your garden - no matter the season.', 'https://perenual.com/storage/species_image/1150_aucuba_japonica_variegata/og/2560px-Aucuba_japonica_27Variegata27_A.jpg', 'Average', '["part shade"]', 'Low', 0, '["Layering Propagation", "Cutting", "Division", "Grafting Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('5c4940ac-0ff4-4d3a-a03e-99d9a0c5cfb8', 'ponytail palm', '["Beaucarnea recurvata"]', 'The ponytail palm (Beaucarnea recurvata) is one of the most interesting houseplants out there. Unique and eye-catching, it has a swollen base and a thin, cascading crown of green foliage, resembling a head of hair. It’s a slow growing and long lived species, making it a great gift to last through generations. Its drought tolerant nature also makes it an excellent choice for neglectful owners. This palm is also incredibly hardy, and can survive in temperatures as low as 10°F. With proper care, it can add a unique, tropical feel to any space.', 'https://perenual.com/storage/species_image/1192_beaucarnea_recurvata/og/51792797390_1b15a8415d_b.jpg', 'Average', '["part sun/part shade"]', 'Low', 0, '["Greenwood Cuttings"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('c7b78223-af84-4abe-a8df-820636c52008', 'begonia', '["Begonia benariensis BIG SERIES"]', 'Begonia benariensis BIG SERIES is an amazing plant species. The plant features beautiful bell-shaped blooms in shades of pink, white and purple and lush, large leaves that make it stand out in the garden. It grows well in full sun or partial shade, making it a perfect option for large containers. With minimal maintenance and tending, Begonia benariensis BIG SERIES produces a long-lasting, low-maintenance display of beautiful color. The blooms attract hummingbirds and butterflies to the garden. With its bright colors and ability to bring beauty to your garden or patio, Begonia benariensis BIG SERIES is an excellent option for any gardener.', 'https://perenual.com/storage/species_image/1195_begonia_benariensis_big_series/og/51286779053_5a7aca2a7b_b.jpg', 'Average', '["full sun", "part shade"]', 'High', 0, '["Stem Propagation", "Leaf Cutting", "Division", "Air Layering Propagation", "Grafting Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('739179d8-40c2-4af1-a18a-49da52eecee5', 'begonia', '["Begonia ''Bepared'' DRAGON WING RED"]', 'Begonia ''Bepared'' DRAGON WING RED is a beautiful, eye-catching plant that can brighten up any outdoor space. This annual begonia features large, bright red leaves that are shaped like a dragon''s wings, up to 10" in length. The foliage adds a dramatic pop of color to your garden or patio. To top it off, this begonia boasts showy clusters of sweetly-scented pink/white blooms that are attractive to butterflies and other pollinators. Easy to maintain and tolerant of many conditions, Begonia ''Bepared'' DRAGON WING RED is a must-have for any garden or landscape.', 'https://perenual.com/storage/species_image/1196_begonia_bepared_dragon_wing_red/og/52542250837_647bf91a34_b.jpg', 'Average', '["part shade", "full sun"]', 'High', 0, '["Stem Propagation", "Leaf Cutting", "Division", "Air Layering Propagation", "Grafting Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('a0d57f95-5f00-4409-b006-ccf6fd2f2e38', 'begonia', '["Begonia boliviensis BOSSA NOVA RED"]', 'Begonia boliviensis BOSSA NOVA RED is an amazing plant species that is sure to add a pop of color to any garden. It has deep red foliage and brilliant orange flowers that bloom during the summer months. The foliage is waxy and thick and its blossoms are delicate and abundant. This perennial grows best in acidic soils and requires plenty of sunlight in order to thrive. It''s an easy care plant that is drought tolerant and requires minimal pruning. Its vibrant colors and low-maintenance nature make it an ideal choice for brightening up gardens in any region.', 'https://perenual.com/storage/species_image/1197_begonia_boliviensis_bossa_nova_red/og/52496552436_073086053b_b.jpg', 'Average', '["full sun", "part sun/part shade"]', 'High', 0, '["Stem Propagation", "Leaf Cutting", "Division", "Air Layering Propagation", "Grafting Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('ffbb8579-61f8-447d-937e-f215a2614a82', 'begonia', '["Begonia ''Canary Wing''"]', 'The Begonia ''Canary Wing'' is an amazing plant species that adds vibrant colors to any garden. Its glossy green foliage is highlighted by eye-catching splashes of yellow, red, bronze, pink and white along its edges. This plant is easy to care for, and will thrive in bright, indirect light. Its long-lasting blooms make it a perfect addition to bouquets, and its cheerful foliage can easily brighten up a backyard patio. It enjoys moist soil, making it versatile enough to be planted outdoors or in a container. Whether used in a garden bed or as an elegant houseplant, the Begonia ''Canary Wing'' will add excitement to any space with its showy blossoms and cheerful foliage.', 'https://perenual.com/storage/species_image/1198_begonia_canary_wing/og/48860938088_382c518a2b_b.jpg', 'Average', '["part shade"]', 'Low', 0, '["Stem Propagation", "Leaf Cutting", "Division", "Air Layering Propagation", "Grafting Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('d8f88202-e61b-4393-bf48-8d66e5cdf928', 'begonia', '["Begonia ''Casper''"]', 'Begonia ''Casper'' is a truly amazing plant. Not only does it boast masses of vibrant white, pink and red blooms, it also has dark green, glossy leaves which are wonderfully textured for an added touch of luxury. It''s an easy-care plant, making it perfect for beginners, and thrives in bright indirect light so it can be planted just about anywhere in your home! It''s low maintenance and doesn''t require frequent watering or pruning making it ultra-convenient, and an excellent choice for anyone who wants a show-stopping floral display with minimum effort.', 'https://perenual.com/storage/species_image/1199_begonia_casper/og/52423452233_02b4dc198b_b.jpg', 'Average', '["part shade", "part sun/part shade"]', 'High', 0, '["Stem Propagation", "Leaf Cutting", "Division", "Air Layering Propagation", "Grafting Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('6fe6d6c3-d41d-4408-8913-7f95344ef983', 'hybrid tuberous begonia', '["Begonia FUNKY PINK"]', 'The hybrid tuberous begonia (Begonia FUNKY PINK) is an amazing species of flower because of its eye-catching bloom that stands out with its bright pink and funky colored petals. It is incredibly easy to take care of and thrives in both warm and cool temperatures, as well as in part shade. Its large showy blossoms will bloom all summer long and keep your garden looking great from spring to fall. They are also easy to propagate with cuttings and can even be grown indoors, adding a splash of unique color to any home. With its beautiful and unique blooms, the hybrid tuberous begonia is an amazing species to have in your garden.', 'https://perenual.com/storage/species_image/1203_begonia_funky_pink/og/Begonia_CorallinaHybrids_PresidentCarnotInflorescenceA.jpg', 'Average', '["part shade", "part sun/part shade"]', 'Low', 0, '["Division", "Stem Propagation", "Leaf Cutting", "Seed Propagation"]', 'High');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('76bda6ad-2798-4132-a6ca-8a9fdeb7e422', 'begonia', '["Begonia ''Green Gold''"]', 'The Begonia ''Green Gold'' is an amazing plant species because of its low maintenance and high impact beauty. The alternating green and gold leaf pattern creates an eye-catching effect in both sunny and shady places. It is known to be particularly tolerant to drought, and is easy to propagate, since it can be grown from a single leaf. Its leaves are oval in shape and have a unique “holly-like” texture, making it easy to identify. This species also produces beautiful pink and yellow flowers, further adding to its beauty. With the Begonia ''Green Gold'', you can liven up any indoor or outdoor space with its vibrant colors and interesting textures.', 'https://perenual.com/storage/species_image/1208_begonia_green_gold/og/Begonia_28Rex-hybrid29_27Merry_Christmas27_02.jpg', 'Average', '["part shade", "part sun/part shade"]', 'Low', 0, '["Stem Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('628d0077-fbfc-4e3f-9a66-862cffde0968', 'begonia', '["Begonia ''Gryphon''"]', 'The begonia ''Gryphon'' is a truly amazing plant. It has a unique feathery, rose-like foliage with purple, cream and green coloration. Its deep pink, double flowers bloom from summer to fall, their petals kissed with yellow stamens for maximum impact. This Begonia can tolerate full shade and make an ideal companion for hostas and ferns. It is exceptionally hardy, being drought and humidity tolerant, and once established, it’s mostly carefree. A real show-stopper in the garden, Begonia ''Gryphon'' is sure to attract plenty of attention and appreciation.', 'https://perenual.com/storage/species_image/1209_begonia_gryphon/og/9656051530_21f82c3dc7_b.jpg', 'Average', '["part sun/part shade", "full sun"]', 'High', 0, '["Stem Propagation", "Leaf Cutting", "Division", "Air Layering Propagation", "Grafting Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('3a9cd86d-754b-49b0-973a-1b492cb00636', 'begonia', '["Begonia ''Martha Stewart''"]', 'The Begonia ''Martha Stewart'' is a beautiful, easy-to-care for option for any garden. It features colorful leaves with yellow and green variegation, as well as spectacular blooms in shades of pink and white. Consequently, it adds a unique and uplifting ambience to the entire space. It is also fairly tolerant of dry air and doesn''t require a lot of water, which makes it an ideal choice for those with busy lifestyles. Additionally, it grows best in partial shade and only needs to be pruned occasionally making it a low-maintenance option. With its unique charm and easy-care routine, the Begonia ''Martha Stewart'' will definitely bring beauty and life to any garden.', 'https://perenual.com/storage/species_image/1211_begonia_martha_stewart/og/52599838828_d48aa4283c_b.jpg', 'Average', '["part shade", "part sun/part shade"]', 'Low', 0, '["Stem Propagation", "Leaf Cutting", "Division", "Air Layering Propagation", "Grafting Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('8fc6f2f3-a1e1-4cc3-bcbd-eccb95e45b64', 'iron cross begonia', '["Begonia masoniana"]', 'Iron Cross Begonia (Begonia masoniana) is a unique and beautiful plant with large, showy foliage. Its green leaves are marked with a deep purple, cross-shaped pattern with white veins radiating outward, giving the leaves their signature "iron cross" look. It''s a great choice for brightening up semi-shaded areas or a low light living space. The foliage develops an interesting tuberous shape over time. It produces clusters of white to pink bell shaped flowers that appear in late summer and fall. It''s truly a spectacular plant that''s sure to be a conversation starter in any garden!', 'https://perenual.com/storage/species_image/1212_begonia_masoniana/og/29865980861_27d35f6447_b.jpg', 'Average', '["part sun/part shade", "filtered shade"]', 'Low', 0, '["Cutting"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('3aa8fbfe-5762-4ea5-9142-071079f9ec8f', 'begonia', '["Begonia ''Penny Lahn''"]', 'Begonia ''Penny Lahn'' is an amazing plant species. Native to tropical regions, its tall branches can reach heights of 24 inches and boast bright green foliage highlighted by magenta-red petals. The large blooms and bold colors make it a great addition to any garden or outdoor space. The leaves and stems are also quite delicate and the plant is fairly easy to take care of. It''s unique look and bright colors make it a standout in any setting - the perfect way to liven up a backyard or potted patio garden. Plus, its low maintenance and easy to care for making it an ideal choice for busy gardeners.', 'https://perenual.com/storage/species_image/1213_begonia_penny_lahn/og/52496552436_073086053b_b.jpg', 'Average', '["part shade", "full shade"]', 'Low', 0, '["Stem Propagation", "Leaf Cutting", "Division", "Air Layering Propagation", "Grafting Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('0fa5a42d-a4db-427c-ab8e-b54f9732adab', 'rex begonia', '["Begonia rex-cultorum"]', 'The Rex Begonia, or Begonia Rex-Cultorum, is truly an amazing type of plant. With its stunning and vibrant colors, intricate and intricate leaf structure, and overall lush appearance, the Rex Begonia is sure to transform any dull looking space. It is a low maintenance plant which is perfect for beginner gardeners and provides lasting beauty with little effort. Furthermore, the Rex Begonia is an air purifying plant which helps to reduce pollutants and toxins in the air. Its stunning foliage, easy maintenance and air purifying qualities make this an amazing and essential addition to any garden.', 'https://perenual.com/storage/species_image/1214_begonia_rex-cultorum/og/28485232700_2b71265572_b.jpg', 'Average', '["part shade", "part sun/part shade"]', 'Low', 0, '["Stem Propagation"]', 'High');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('860a7060-d415-42fd-a310-11ae16e26135', 'rex begonia', '["Begonia rex-cultorum ''Super Curl''"]', 'Rex begonia (Begonia rex-cultorum ''Super Curl'') is amazing for its stunning foliage coloration and uniquely sculpted leaves. This shade-loving plant has stunning leaves that are usually bright green, with patches of silver, pink, and red. The ''Super Curl'' cultivar tends to have more intense coloration and the elongated, curly leaves look almost sculpted. Its showy blooms, soft to the touch, come in a variety of shades, ranging from pink to white. Rex begonia is incredibly hardy, making it an easy choice for a variety of gardens. It''s a standout addition to any shady area, and its unique colors and sculptural leaves are sure to make an impact.', 'https://perenual.com/storage/species_image/1215_begonia_rex-cultorum_super_curl/og/28152488224_06e2aca626_b.jpg', 'Average', '["part shade", "part sun/part shade"]', 'Low', 0, '["Stem Propagation", "Leaf Cutting", "Division", "Air Layering Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('59e11f97-ac99-4177-8de4-7c931847f3ca', 'begonia', '["Begonia ''River Nile''"]', 'The Begonia ''River Nile'' is an amazing plant species due to its beautiful colors and impressive hardiness. Its striking pink and white flowers trailing from its long stems will brighten up any room or garden. This attractive variety is also very easy to care for and can be grown in temperatures as low as 58°F. It loves the sun, so it''s great for brightening up darker areas, and loves to be watered regularly. Its foliage is a rich deep green, making it perfect for borders or raised beds and it''s sure to add an exotic touch to any garden.', 'https://perenual.com/storage/species_image/1216_begonia_river_nile/og/48273820461_444a667bea_b.jpg', 'Average', '["part shade", "part sun/part shade"]', 'Low', 0, '["Stem Propagation", "Leaf Cutting", "Division", "Air Layering Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('7be01b79-5a8e-4bc0-93a7-5750ac6f87e1', 'begonia', '["Begonia (Semperflorens Cultorum Group)"]', 'The Begonia (Semperflorens Cultorum Group) is renowned for its showy and eye-catching display of vibrant pink and white flowers. These flowers are often referred to as being “non-stop” bloomers, as they will continually flower from late spring all the way through to autumn. This amazing species is incredibly hardy as well, making it suitable for both indoor and outdoor planting. It is also able to thrive in either shady or full sun environments, adding to its appeal. Begonia (Semperflorens Cultorum Group) is an excellent choice if you’re looking for a long-flowering and hardy plant that will bring color and cheer to any garden.', 'https://perenual.com/storage/species_image/1217_begonia_semperflorens_cultorum_group/og/28769541485_d149269ecc_b.jpg', 'Average', '["part shade", "part sun/part shade"]', 'Low', 0, '["Stem Propagation", "Leaf Cutting", "Division", "Air Layering Propagation", "Grafting Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('ddbe96d3-f5f8-4130-88c9-a88ef413b15a', 'begonia', '["Begonia semperflorens ''Senator Deep Rose''"]', 'The Begonia semperflorens ''Senator Deep Rose'' is a show stopping variety of houseplant. With its beautiful green and deep rose foliage, and its unique fan-like growth habit, it stands out from the crowd. The flowers are free-flowering and come in a variety of colors, from bright pink to deep yellow, giving it a lovely, vibrant display all year round. The Begonia semperflorens ''Senator Deep Rose'' is perfect for brightening up your office or home decor and is also easy to care for, making it an excellent choice for any home or garden.', 'https://perenual.com/storage/species_image/1218_begonia_semperflorens_senator_deep_rose/og/8884235929_8f2eaa2082_b.jpg', 'Average', '["part shade", "part sun/part shade"]', 'Low', 0, '["Stem Propagation", "Leaf Cutting", "Division", "Air Layering Propagation", "Grafting Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('481931af-8c39-435e-b2de-9c47b5f6fe26', 'wax begonia', '["Begonia semperflorens-cultorum ''Harmony Scarlet''"]', 'The wax begonia (Begonia semperflorens-cultorum ''Harmony Scarlet'') is an amazing plant species with remarkable benefits. It''s glossy, dark green leaves with red accents are stunning and make for vibrant plant displays. This evergreen variety is tolerant of a wide range of temperatures, and can even bloom continuously throughout the summer. Best of all, the wax begonia is an easy-care option that requires little maintenance! Water in moderation and plant in a bright, well-drained spot and you will be sure to enjoy the flowers'' unique beauty over a long season.', 'https://perenual.com/storage/species_image/1219_begonia_semperflorens-cultorum_harmony_scarlet/og/52423452233_02b4dc198b_b.jpg', 'Average', '["part shade", "filtered shade"]', 'Low', 0, '["Stem Propagation", "Leaf Cutting", "Division", "Air Layering Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('6f863b90-6d46-4e34-ac9b-cfd6df92d855', 'angelwing begonia', '["Begonia ''Sinbad''"]', 'Angelwing begonias (Begonia ''Sinbad'') are a luscious and eye-catching plant species. They’re easy to care for, yet beautiful enough to draw attention. These begonias are both drought and heat-resistant, making them suitable for any garden setting. They have large, glossy green foliage with burgundy highlights, and delicate white flowers. The light-pink, wavy petals of the ''Sinbad'' variety give the plant its unique name, as they resemble the wings of an angel. With proper care, these begonias will provide year-round foliage and seasonal flower blossoms. Perfect for adding colour to a outdoor space or as a houseplant, angelwing begonias are an amazing addition to any garden.', 'https://perenual.com/storage/species_image/1220_begonia_sinbad/og/3789073932_9453547ff0_b.jpg', 'Frequent', '["part shade", "part sun/part shade"]', 'Low', 0, '["Stem Propagation", "Leaf Cutting", "Division", "Air Layering Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('d36afb6e-247b-402e-9f88-489bdb094867', 'angelwing begonia', '["Begonia ''Sophia''"]', 'The Angelwing Begonia ''Sophia'' is an amazing plant species with unique characteristics that make it stand out. Its large, oval-shaped leaves feature striking red undersides that contrast beautifully with the glossy, deep green tops. The showy, double pink flowers bloom throughout the year, adding to its beauty. Plus, Sophia is incredibly easy to care for, needing minimal attention and well-draining soil to grow its best. Its an impressive sight indoors, brightening any room with its exotic flair. With its graceful lines and attractive color combinations, the Angelwing Begonia ''Sophia'' is a charming, eye-catching addition to any home.', 'https://perenual.com/storage/species_image/1222_begonia_sophia/og/Begonia_27Sophie_CC3A9cile27_1.jpg', 'Average', '["part shade", "part sun/part shade"]', 'Low', 0, '["Stem Propagation", "Leaf Cutting", "Division", "Air Layering Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('6ceddb5a-8086-4cbf-b935-3779735dd97b', 'hybrid tuberous begonia', '["Begonia tuberhybrida ''Jurassic Pink Splash''"]', 'The hybrid tuberous begonia (Begonia tuberhybrida ''Jurassic Pink Splash'') is an amazing garden specimen that blooms heavily in shades of pink and white. Its frilly and vibrant blooms add a tropical aesthetic to any space. The powerhouse of a plant can grace any garden all season long with its clusters of flowers, and is relatively easy to care for. It flourishes in partial sun to shady conditions and only requires minimal watering, making it an efficient use of resources for any home gardener. With its colorful and long-lasting blooms, this begonia is sure to be a star in your garden!', 'https://perenual.com/storage/species_image/1223_begonia_tuberhybrida_jurassic_pink_splash/og/22656824786_be81bc2887_b.jpg', 'Minimum', '["part shade"]', 'Low', 0, '["Division", "Stem Propagation", "Leaf Cutting", "Seed Propagation", "Air Layering Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('c4ea27c0-3a98-4ede-8ea6-e2be83738985', 'hybrid tuberous begonia', '["Begonia tuberhybrida ''Jurassic Silver Swirl''"]', 'The hybrid tuberous begonia ''Jurassic Silver Swirl'' is simply amazing. It features stunning, silver-green foliage that develops a festive hue of pink and purple shades as it matures. This begonia is low-growing and upright, with interesting branching that adds multiple levels of texture. Bright green and yellow flowers bloom in the summer months, making it a show-stopping feature in the garden. In addition, ''Jurassic Silver Swirl'' is resistant to deer, easy to care for, and loves partial to full shade. Its intricate leaves provide year-round interest, making this begonia a real asset to the garden.', 'https://perenual.com/storage/species_image/1224_begonia_tuberhybrida_jurassic_silver_swirl/og/28152488224_06e2aca626_b.jpg', 'Average', '["part shade", "part sun/part shade"]', 'Low', 0, '["Division", "Cutting", "Air Layering Propagation", "Leaf Cutting", "Seed Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('060efb59-800d-45b0-8533-27f1586b5591', 'hybrid tuberous begonia', '["Begonia (Tuberosa Group)"]', 'Hybrid Tuberous Begonia is a stunning addition to any garden or home. Its unique flowers come in a variety of colors, from white to pink, orange and yellow. Its large, toothed leaves are attractive, and its tuberous rhizomes allow it to thrive in a range of climates. When planted in semi-shade, the leaves and blooms of the Hybrid Tuberous Begonia will remain vibrant and healthy. This beauty can be appreciated all year round indoors or out! With its hardiness and easy care, this is a great plant for everyone, from the novice gardener to the experienced green thumb.', 'https://perenual.com/storage/species_image/1226_begonia_tuberosa_group/og/Begonia_x_tuberhybrida_1005Pink1.jpg', 'Average', '["part sun/part shade", "full sun"]', 'Low', 0, '["Division", "Stem Propagation", "Leaf Cutting", "Seed Propagation", "Air Layering Propagation"]', 'High');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('f11a11bc-0fa6-4788-8ef6-62e7be52948c', 'jewel plant', '["Bertolonia maculata"]', 'The Jewel Plant (Bertolonia maculata) is an astounding tropical plant that provides an array of colors and gratification to its beholders! Producing white and purple speckled, bell-shaped flowers in the center, and textured, intricate foliage that creates a sensational palette of variations of deep and light green hues, it’s no wonder why this plant is everyone’s favorite. Low maintenance and beautiful, the Jewel Plant is great for any home, garden or even a patio. With its unique and attractive features, it is no wonder why this plant is beloved by gardeners, landscape architects, and interior designers alike.', 'https://perenual.com/storage/species_image/1272_bertolonia_maculata/og/Bertolonia_maculata_2019-04-16_0864.jpg', 'Frequent', '["part shade", "part sun/part shade"]', 'Low', 0, '["Stem Propagation", "Leaf Cutting", "Division", "Air Layering Propagation", "Grafting Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('d72b6fce-3c7f-4b99-95a6-0d690e8a50fc', 'angel wings', '["Caladium bicolor"]', 'Angel Wings (Caladium bicolor) provides a vibrant splash of color to gardens and outdoor spaces. It''s beautiful gray-green veined leaves come in a variety of hues including pink, white, red and burgundy. The heart-shaped foliage is also perfect as a border plant and as a backdrop in beds and borders. Angel Wings is a native of the tropic and can grow up to 60cm in height and 40cm in width. It is very low maintenance and can thrive in both full sun and part shade. It’s also quite resistant to pests and disease which makes it an ideal choice. A great addition to any garden or outdoor space, Angel Wings is definitely worth having in your garden!', 'https://perenual.com/storage/species_image/1457_caladium_bicolor/og/25575875658_d782fb76f1_b.jpg', 'Average', '["part shade", "part sun/part shade"]', 'Low', 0, '["Division"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('44b9fd02-f2e3-4e9a-9586-9367c9e5bad5', 'rattlesnake plant', '["Calathea lancifolia", "Goeppertia insignis"]', 'Calathea lancifolia, commonly known as rattlesnake plant, is an amazing houseplant that has extra-ordinary foliage. The leaves are long, narrow and wavy, like snakeskin, and have alternating white and dark green stripes. When its leaves move, the leaves gently shake which earns it its name! Rattlesnake plants are unique and hardy plants that require medium light and moist but well-drained soil. They are also non-toxic and easy to care for which make them a great houseplant for any environment. The wavy foliage will add beauty and movement to any room, making rattlesnake plants a great choice for any gardener.', 'https://perenual.com/storage/species_image/1469_calathea_lancifolia/og/3676781257_2e8730f471_b.jpg', 'Average', '["part shade", "part sun/part shade"]', 'Low', 0, '["Division", "Stem Propagation", "Leaf Cutting", "Air Layering Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('8db39cdf-f80d-4184-b4c8-476fd69eb4bd', 'peacock plant', '["Calathea makoyana"]', 'The Peacock Plant (Calathea makoyana) is an amazing plant with unique foliage. Its striking leaves boast a bright green color on the surface and deep purple color on the underside. These attractive leaves grow in an interesting fan shape, possessing the look of a peacock''s grand display of feathers. Plus, as the plant''s leaves mature and expand they form a unique ''stair'' pattern, giving the plant a special visual appeal. Moreover, the plant repels pests such insects, and many have said that it fills the room with a pleasant fragrance. To top it all off, the Peacock Plant is incredibly easy to care for and is a great choice for any houseplant enthusiast.', 'https://perenual.com/storage/species_image/1470_calathea_makoyana/og/5455983461_9d5ac395aa_b.jpg', 'Average', '["part shade", "part sun/part shade"]', 'Moderate', 0, '["Division"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('c8f86cc8-ec19-4459-9071-f469f3a2e23e', 'zebra plant', '["Calathea zebrina"]', 'The zebra plant (Calathea zebrina) is truly an amazing species. It is an evergreen, tropical perennial native to Bolivia and Brazil that features striking deep green leaves with bright yellow and dark green stripes. This bold foliage is complemented by pink and purple flowers that bloom at night. The zebra plant does best in high humidity and moderate sunlight, so it is a popular choice for indoor gardens. It’s also a low-maintenance choice for gardening enthusiasts – only requiring to be watered once or twice a week. The zebra plant is also a great choice to help clean your indoor air. Its foliage acts as a natural air filter, removing toxins from the air. So you can enjoy its amazing foliage all while getting the benefits of cleaner air.', 'https://perenual.com/storage/species_image/1471_calathea_zebrina/og/24798559861_0c6830ba26_b.jpg', 'Average', '["part shade", "part sun/part shade"]', 'Low', 0, '["Division", "Stem Propagation", "Air Layering Propagation", "Leaf Cutting"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('61b91af5-0944-424a-8a37-a55a102e3097', 'ornamental pepper', '["Capsicum annuum ''Black Pearl''"]', 'Ornamental pepper (Capsicum annuum ''Black Pearl'') is an amazing plant species due to its unique features and dramatic colour display. These peppers produce small conical fruits that change colour from black to bright yellow when ripe, creating a stunning visual display in the garden. The plant is incredibly low maintenance, requiring no pruning or staking, and will thrive in direct sunlight. The ''Black Pearl'' is also unique in its ability to show off its stunning peppers year-round, making it a perfect choice for an all-season garden showpiece.', 'https://perenual.com/storage/species_image/1597_capsicum_annuum_black_pearl/og/2560px-Capsicum_annuum_Black_Pearl_6zz.jpg', 'Average', '["full sun"]', 'Low', 0, '["Seed Propagation", "Cutting", "Layering Propagation", "Division", "Grafting Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('51c6bf58-87e9-4412-a8e7-d8b5df346c6e', 'ornamental pepper', '["Capsicum annuum ''Purple Flash''"]', 'Ornamental pepper (Capsicum annuum ''Purple Flash'') is an amazing plant species with eye-catching purple foliage and stems. In addition to it''s beautiful foliage, it produces small purple fruit with a brilliant lime green interior. Popular among gardeners for its ornamental beauty, it is easy to care for and drought tolerant. It grows best in light, well-drained soils and will thrive in full sun or partial shade. Ornamental pepper is a great choice for adding dramatic color to gardens and containers and can even be used in cooking for a unique flavor.', 'https://perenual.com/storage/species_image/1603_capsicum_annuum_purple_flash/og/1536px-Capsicum_annuum_27Peruvian_Purple27_kz01.jpg', 'Average', '["full sun"]', 'Low', 0, '["Seed Propagation", "Cutting", "Layering Propagation", "Division", "Grafting Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('3b8facc5-81c9-4af3-93c0-e9c7f4a1bbe0', 'orchid', '["Cattleya (group)"]', 'The orchid (Cattleya group) is an amazing plant species. Its striking and eye-catching appearance sets this flower apart from the rest. Its large and colorful blooms, ranging in a variety of stunning purple, pink, white, and yellow hues, are truly remarkable. Its distinctive petals and intricate patterns make it extremely unique. Its versatile nature allows it to thrive in various conditions and environments. Even during winter, the orchid continues to provide an exotic display of beauty with each flower. As a result, the orchid has become an iconic symbol of long-lasting beauty and grace.', 'https://perenual.com/storage/species_image/1716_cattleya_group/og/orchid-flower-colombia-flowers.jpg', 'Average', '["part shade", "part sun/part shade"]', 'Low', 0, '["Division", "Air Layering Propagation", "Tissue Culture", "Seed Propagation"]', 'Moderate');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('e1ce82b3-014b-4bb8-9e67-ac5a8281b488', 'parlor palm', '["Chamaedorea elegans"]', 'The Parlor Palm (Chamaedorea elegans) is an amazing house plant with several incredible attributes. It is a very slow growing species, requiring minimal maintenance. It is also quite small, so it fits perfectly in spaces such as desks, tabletops or counters. This species thrives in medium to low light conditions and requires little water, making it an easy addition to even the most neglectful caretakers. Additionally, this tropical species has beautiful, deep green leaves and graceful fronds that add a lush, exotic texture to any home. Its air purifying quality and affordability make it a great choice for anyone looking to add a bit of nature and beauty to their home.', 'https://perenual.com/storage/species_image/1820_chamaedorea_elegans/og/24981394791_176142abd3_b.jpg', 'Average', '["part shade"]', 'Low', 0, '["Seed Propagation"]', 'Low');
INSERT INTO public.plant (plant_id, plant_name, plant_species, plant_description, plant_image_url, plant_watering, plant_sunlight, plant_growth_rate, plant_toxicity, plant_propagation, plant_maintenance) VALUES ('a038dcd0-eda6-4210-8405-8d9e76e494fd', 'chamaedorea', '["Chamaedorea microspadix"]', 'Chamaedorea microspadix is an incredible plant species! With its slender, deep green leaves and delicate white stripes near the midrib of each leaf, it adds a unique touch to any space. It is a very low-maintenance plant, requiring little more than a misting from a spray bottle. Additionally, it is tolerant to low light, though it thrives in bright, indirect light. With fuss-free care and striking foliage, this plant makes a perfect addition to any home.', 'https://perenual.com/storage/species_image/1821_chamaedorea_microspadix/og/26815590515_805ab5b081_b.jpg', 'Frequent', '["part shade"]', 'Low', 0, '["Division", "Cutting", "Air Layering Propagation", "Seed Propagation"]', 'Low');
